svg.script:
const el = document.getElementById("type");
const cursor = document.getElementById("cursor");

const words = [
  "Hello World",
  "Olá Mundo",
  "Bem-vindo",
  "Carregando...",
  "Adrian Razini"
];

let i = 0;
let char = 0;
let deleting = false;

function typeLoop() {
  let word = words[i];

  if (!deleting) {
    // escrevendo
    el.textContent = word.substring(0, char + 1);
    char++;

    if (char === word.length) {
      deleting = true;
      setTimeout(typeLoop, 900);
      return;
    }
  } else {
    // apagando
    el.textContent = word.substring(0, char - 1);
    char--;

    if (char === 0) {
      deleting = false;
      i = (i + 1) % words.length;
    }
  }

  setTimeout(typeLoop, deleting ? 60 : 90);
}

// cursor piscando
setInterval(() => {
  cursor.style.opacity = cursor.style.opacity === "0" ? "1" : "0";
}, 500);

typeLoop();
